import React, { useState } from 'react'
import { Box, Accordion, AccordionSummary, AccordionDetails, Typography, Container, Stack } from '@mui/material';
import pic from '../Media/faqs.png'
import db from '../DbFile.json'

export default function Faqs() {
    const[questions,setQuestions]=useState(db.SMarket_Q)
    const toggleQuestion = (index) => {
        const newQuestions = [...questions];
        newQuestions[index].isOpen = !newQuestions[index].isOpen;
        setQuestions(newQuestions);
      };
     
  return (
    <div style={{marginTop:'90px'}}>
       <Container sx={{ display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center'}}>
      <Typography  sx={{color:'#7B3AE2',fontSize:'30px',fontWeight:'bold'}}>
      FAQs
      </Typography>
      </Container>

      <Stack direction='row'>
        <Container>
        {questions.map((q, index) => (
      <Accordion
        sx={{
          borderBottom: '2px solid #7D4896',
          width: '60%',
          marginLeft: '0%',
          bgcolor: 'transparent',
          '&:before': { display: 'none' },
          padding: '20px',
          boxShadow: 'none',
          '&:last-child': {
            borderBottom: '2px solid #7D4896',
            width: '60%',
            marginLeft: '0%',
            borderRadius: '0px'
          }
        }}
        elevation={0}
        disableGutters
        expanded={q.isOpen}
        onChange={() => toggleQuestion(index)}
      >
        <AccordionSummary>
          {q.isOpen ? (
            <RemoveIcon sx={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }} />
          ) : (
            <AddIcon sx={{ color: '#7D4896', marginRight: '20px', marginTop: '5px' }} />
          )}
          <Typography sx={{ color: '#7D4896', fontSize: '20px', fontWeight: 'bold' }}>
            {q.question}
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography sx={{ color: '#7D4896', fontSize: '16px' }}>
            {q.answer}
          </Typography>
        </AccordionDetails>
      </Accordion>
  ))}
        <img src={pic}/>
        </Container>
      </Stack>
    </div>
  )
}
