import { Box, Container, Typography, useMediaQuery } from '@mui/material'
import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import { Autoplay,Navigation } from 'swiper/modules'; 
import '../App.css'
import 'swiper/css/navigation';
import pic from '../Media/Icons.png'

export default function Features() {
    const matches = useMediaQuery('(min-width:1000px)');
    const swiperStyles = `
    @media (max-width: 500px) {
      .swiper-button-next,
      .swiper-button-prev {
        display: none;
      }
    }
  
    .swiper-button-next,
    .swiper-button-prev {
      color: #C71B1B;
    }
  `;
  return (
    <div>
        <Box sx={{ display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center'}}>
      <Typography  sx={{color:'#7B3AE2',fontSize:'30px',fontWeight:'bold'}}>
      Why Is *Name* Needed
      </Typography>
      </Box>
      <style>{swiperStyles}</style>
<Container maxWidth='lg' sx={{mt:'30px'}}>
      <Swiper
      modules={[Autoplay,Navigation]} 
      autoplay={{
       delay: 5000,
       disableOnInteraction: false,
        }}
        navigation
        slidesPerView={matches?3:1}
        spaceBetween={20}
        style={{paddingBottom:'50px' }}
      >
        <SwiperSlide>
        <div class="card-container">
        <img  src={pic} alt="user" style={{width:'80px',height:'80px'}}/><br/>

	<p>“#333134 </p>
	<p style={{marginTop:'30px'}}>Jesika Lynn - 2/5/2024</p>
</div>
        </SwiperSlide>
        <SwiperSlide>
        <div class="card-container">
	<p>“Moe’s attention to detail is
    unmatched. <br/> In the finished” </p>
	<p style={{marginTop:'30px'}}>Jesika Lynn - 2/5/2024</p>
</div>
        </SwiperSlide>
        <SwiperSlide>
        <div class="card-container">
	<p>“Moe’s attention to detail is
    unmatched. <br/> In the finished” </p>
	<p style={{marginTop:'30px'}}>Jesika Lynn - 2/5/2024</p>
</div>
        </SwiperSlide>
        </Swiper>
        </Container>
    </div>
  )
}
