import React, { useRef, useState } from 'react'
import { Box, Container, Stack, Typography, useMediaQuery } from '@mui/material'
import vid from '../Media/Social Media Marketing Video.mp4'
import pic from '../Media/Group 9755.png'

export default function Video() {
    const [isPlaying, setIsPlaying] = useState(false);
    const videoRef = useRef(null);
    const handlePlayPause = () => {
        const video = videoRef.current;
        if (isPlaying) {
          video.pause();
        } else {
          video.play();
        }
        setIsPlaying(!isPlaying);
      };
  return (
    <div style={{position:'relative'}}>
       <Container sx={{ display:'flex',flexWrap:'wrap',justifyContent:'center',alignItems:'center'}}>
      <Typography  sx={{color:'#7B3AE2',fontSize:'30px',fontWeight:'bold'}}>
      So Simple. So Efficient.
      </Typography>
      </Container>

      <Container maxWidth='md' sx={{mt:'20px', bgcolor:'#D1D1D1',borderRadius:'40px',padding:'0px'}}>
       <video ref={videoRef} src={vid} style={{height:'550px',width:'100%',borderRadius:'40px'}} />
       {isPlaying ? '' : <img src={pic}  onClick={handlePlayPause} style={{height:'50px',position:'absolute'
       ,top:'50%',right:'50%',cursor:'pointer'}}/>}
      </Container>
    </div>
  )
}
